function utf16to8(str) {
    var out, i, len, c;
    out = "";
    len = str.length;
    for(i = 0; i < len; i++){
        c = str.charCodeAt(i);
        if ((c >= 0x0001) && (c <= 0x007F)) {
            out += str.charAt(i);
        } else if (c > 0x07FF){
            out += String.fromCharCode(0xE0 | ((c >> 12) & 0x0F));
            out += String.fromCharCode(0x80 | ((c >> 6) & 0x3F));
            out += String.fromCharCode(0x80 | ((c >> 0) & 0x3F));
        } else {
            out += String.fromCharCode(0xC0 | ((c >> 6) & 0x1F));
            out += String.fromCharCode(0x80 | ((c >> 0) & 0x3F));
        }
    }
    return out;
}

function getSign(arrInput) {
    // strArrInput = getQueryString(arrInput)
    var str = '';
    for(var i in arrInput.sort()){
        str=str+arrInput[i]+'&'
    }
    str2 = str.substr(0, str.length-1);
    postman.setGlobalVariable("str2", str2);

    return hex_md5(hex_sha1(utf16to8(str2)));
    }

function getHttpkeyvalue(keyvalue) {

    var paramlist = new Array();
    var bianliangdict = new Object();
    var realurl = '';

    tmp = keyvalue.split('}}');
    for (var i = 0, len = tmp.length; i < len; i++) {
        var str = tmp[i];
        if (str.search('{{') !== -1) {
            var list = str.split('{{');
            var str0 = list[0];
            var str1 = list[1];
            if (str1 === 'sign'){
                var list1 = str0.split('&sign');
                if (list1[0] === 0){
                    console.log("这是个sign参数，可以忽略不要");
                }else{
                    var str2 = list1[0];
                    paramlist.push(str2);
                }

            }else{
                if (str0.length === 0) {
                    var value0 = postman.getEnvironmentVariable(str1);
                    paramlist.push(str1);
                    bianliangdict[str1] = value0;
                } else {
                    var value1 = postman.getEnvironmentVariable(str1);
                    paramlist.push(str0);
                    paramlist.push(str1);
                    bianliangdict[str1] = value1;
                }
            }

        } else if (str === 0) {
            console.log("这是一个控制符传，跳过");
        } else {
            paramlist.push(str);
        }
    }

    console.log("列表值长度"+paramlist.length);
    var result = '';
    for (var i= 0,len=paramlist.length; i<len; i++){
        var str = paramlist[i].toString();
        if(str === 0){
            console.log('用户传了一个空字符串');
        }else if(str === "sign"){
            document.write("第二个中" +str +  "<br>");
        }else if(str in bianliangdict){
            var test = postman.getEnvironmentVariable(str);
            realurl += test;
        }else{
            realurl += str;
        }
    }
    return realurl;
}

